    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        Usuario usuario = usuarioDao.login(jTxtLogin.getText(), jPassword.getText());
        
        if(usuario.getId() == null) {
            JOptionPane.showMessageDialog(this, "Tente novamente", "Usu√°rio ou senha incorretos", JOptionPane.ERROR_MESSAGE);
        } else {
            System.out.println(usuario);
            FrmTelaPrincipal tela = new FrmTelaPrincipal(usuario);
            
            tela.setVisible(true);
            
            this.dispose();
        }
    }//GEN-LAST:event_btnLoginActionPerformed

